import React, { useState } from 'react';
import axios from 'axios';
import '../index.css';
import { useNavigate } from 'react-router-dom';

const Top: React.FC = () => {
  const url = "/works";

  const navigate = useNavigate();
  let isLoggedIn: boolean = false;
  if (localStorage.getItem('user_id')){
    const isLoggedIn: boolean = true;
  }

  const LogoutSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    let url: string = "/users/" + localStorage.getItem('user_id') + "/logout";
    const afterUrl: string = window.location.href;

    try {
      await axios.post(url)
      .then((response) => {
        localStorage.clear();
        navigate(afterUrl);
      });
    } catch (error) {
      console.error(error);
    }

  }

  const AuthenticationComponent = () => {
    if (isLoggedIn){
      return (
        <button onSubmit={LogoutSubmit}>ログアウト</button>
      );
    } else {
      return (
        <div>
          <a href="/users/login">ログイン</a>
          <a href="users/register">新規登録</a>
        </div>
      );
    }
  }

  return (
    <div className="App">
      <h1 className="bg-gray-300">Topページ</h1>
      <div>{localStorage.getItem("user_id")}</div>
      { AuthenticationComponent }
    </div>
  );
}

export default Top;
